var benim_interval =[];
var benim_yuzde;

socket.on('opsiyon_me', function(data){

  var product_id = data;
  var son_id =product_id;

  if (benim_interval[product_id]){
  	  	clearInterval(benim_interval[product_id]);
  	  	$('.product_item[data_id="'+son_id+'"] .son_fiyattan_hemen_al').find('.geri').remove();
  	  	$('.product_item[data_id="'+son_id+'"] .son_fiyattan_hemen_al').find('.geri2').remove();
  }
  $('.product_item[data_id="'+son_id+'"] .son_fiyattan_hemen_al').text(".");
  $('.product_item[data_id="'+son_id+'"] .son_fiyattan_hemen_al').append('<div class="geri" style="width:100%;"></div><div class="geri2" style="width:100%;"></div>');
  benim_interval[product_id] = setInterval(function(){ buton_background_color(son_id); }, 200);


}); 
 
 

function buton_background_color (urun_id) {
	var toplam_genislik = $('.product_item[data_id="'+urun_id+'"] .son_fiyattan_hemen_al').width();
	var butonum = $('.product_item[data_id="'+urun_id+'"] .son_fiyattan_hemen_al .geri');
	var percent = butonum.width();
  var urun_id = urun_id;
	if (butonum.width()<5) {
		clearInterval(benim_interval[urun_id]);
		butonum.remove();
		$('.product_item[data_id="'+urun_id+'"] .son_fiyattan_hemen_al .geri2').remove();
		$('.product_item[data_id="'+urun_id+'"] .son_fiyattan_hemen_al').text("Son Fiyattan hemen al");
    $('.product_item[data_id="'+urun_id+'"] .son_fiyat_goster').removeClass('sizin_eski').addClass('sizin');
    $('.product_item[data_id="'+urun_id+'"] .son_fiyattan_hemen_al').removeClass("disabled");
		return;
	} 

	butonum.width(percent-toplam_genislik/50);
	$('.product_item[data_id="'+urun_id+'"] .son_fiyattan_hemen_al .geri2').text(""+parseInt(butonum.width()/toplam_genislik*10)+" saniye bu fiyata sadece sizindir, HemenAl"); 
}




