function keyboard (event,gidecek,nesne) {
  var keyCode = ('which' in event) ? event.which : event.keyCode;
  if (keyCode == 13) {
    if (gidecek) {
      socket.emit('msg',{product_id: $(nesne).attr('product_id'),msg: gidecek});
    }
    $(nesne).val('');
    return false;
  }
}



socket.on('msg', function(data){
  if (data.user_id==user_id) {
     $('.chat-messages[data_id="'+data.product_id+'"] .messages').append('<div class="message"><span class="gonderen" style="color:red;">Siz:</span> '+data.msg+'</div>');
     scroll_bottom(data.product_id);
  } else {
     var renk_sira = data.user_id%tum_renkler.length;
     $('.chat-messages[data_id="'+data.product_id+'"] .messages').append('<div class="message"><span class="gonderen" style="color:'+tum_renkler[renk_sira]+';">'+data.user_name+':</span> '+data.msg+'</div>');
      scroll_bottom(data.product_id);
  }
  return;
});



socket.on('son_10_msg', function(data){

  for (var i = data.length-1; i >=0; i--) {
     if (data[i].user_id==user_id) {
     $('.chat-messages[data_id="'+data[i].product_id+'"] .messages').append('<div class="message"><span class="gonderen" style="color:red;">Siz:</span> '+data[i].msg+'</div>');
     scroll_bottom(data[i].product_id);
    } else {
       var renk_sira = data[i].user_id%tum_renkler.length;
       $('.chat-messages[data_id="'+data[i].product_id+'"] .messages').append('<div class="message"><span class="gonderen" style="color:'+tum_renkler[renk_sira]+';">'+data[i].user_name+':</span> '+data[i].msg+'</div>');
       scroll_bottom(data[i].product_id);
    }
  }


  return;
});


