$( "body" ).on( "click", ".sizin", function(e) {
    if ($('.product_item[data_id="'+$(this).attr('product_id')+'"] .sonra_fiyat_dustu').length) {
       socket.emit("get_product_data",$(this).attr('product_id'));
       return;
    }
    e.stopPropagation();
    $(this).popover({content: "Şuanda Son Fiyatı görüyorsunuz, Son Fiyattan satın alabilirsiniz.", placement: 'top',trigger: 'manuel'});
    $(this).popover('show');


});

