var giris_kontrol_int = setInterval(function(){ checklogin(); }, 5000);



function checklogin () {

  if (login==0) {
     clearInterval(giris_kontrol_int);
     return;
  } else {
     $.getJSON(home_url+"Login/ajax", function(data) { 
        if (data.status==0) {
          login=0;
          bootbox.alert(data.msg,function(result){ 
             window.location = home_url+"Login/giris";
          });
        }
      }); 
  }
 
}